using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace Grades{    class Program    {        static void Main(string[] args)         {            GradeBook book = new GradeBook();            try            {                Console.WriteLine("Enter a name");                book.Name = Console.ReadLine();            }            catch (ArgumentException ex)            {                Console.WriteLine(ex.Message);            }                        book.AddGrade(91);            book.AddGrade(75);            book.AddGrade(89.5f);            // Console.Out let's us specifiy where we want to write the grades to (console or some file for example)            book.WriteGrades(Console.Out);                        GradeStatistics stats = book.ComputeStatistics();            WriteResult("Average", stats.AverageGrade);            // C# type cast (int) to change to int value            WriteResult("Highest", stats.HighestGrade);            WriteResult("Lowest", stats.LowestGrade);            WriteResult(stats.Description, stats.LetterGrade);        }                static void WriteResult(string description, string result)        {            // cw using formatting string where 0 is the first arg passed in and 1 is the second arg given            // Console.WriteLine("{0}: {1}", description, result);            Console.WriteLine($"{description}: {result}");        }                static void WriteResult(string description, float result)        {            // cw using formatting string where 0 is the first arg passed in and 1 is the second arg given            // Console.WriteLine("{0}: {1}", description, result);            Console.WriteLine($"{description}: {result:F2}");        }    }}