using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace Grades{    class Program    {        static void Main(string[] args)         {            GradeBook book = new GradeBook();            book.AddGrade(91);            book.AddGrade(75);            book.AddGrade(89.5f);            GradeStatistics stats = book.ComputeStatistics();            WriteResult("Average", stats.AverageGrade);            // C# type cast (int) to change to int value            WriteResult("Highest", (int)stats.HighestGrade);            WriteResult("Lowest", stats.LowestGrade);        }        static void WriteResult(string description, int result)        {            Console.WriteLine(description + ": " + result);        }        static void WriteResult(string description, float result)        {            Console.WriteLine(description + ": " + result);        }    }}